import React from 'react'
import TodoList from './Todo/TodoList'

function App() {
    let todos = [
        {id: 1, completed: false, title: 'buy milk'},
        {id: 2, completed: false, title: 'buy chok'},
        {id: 3, completed: false, title: 'buy gus'}
    ]
    function toggleTodo(index){
        // todos = todos.map(todo => {
        //     if (todo.id === id) {
        //         todo.completed = !todo.completed
        //         console.log(`${todo.id} ${todo.completed}`)
        //     }
        //     return todo
        // })
        todos[index].completed = !todos[index]
        console.log(`${todos[index].id} ${todos[index].completed}`)
        return todos[index]

    }

  return (
      <div className='wrapper'>
          <h1>First react example</h1>

          <TodoList todos={todos} onToggle={toggleTodo}/>
      </div>
  )
}

export default App;
