import React from 'react'
import TodoList from './Todo/TodoList'
import Context from "./context"
import addTodo from "./Todo/addTodos"

function App() {
    const [todos, setTodos] = React.useState(
        [
        {id: 1, completed: true, title: 'buy milk'},
        {id: 2, completed: false, title: 'buy chok'},
        {id: 3, completed: false, title: 'buy gus'}
    ])
    function toggleTodo(id){
        setTodos(
            todos.map(todo => {
                    if (todo.id === id) {
                        todo.completed = !todo.completed
                        console.log(`${todo.id} ${todo.completed}`)
                    }
                    return todo
            })
        )
        // setTodos(() => {
        //     todos[index].completed = !todos[index].completed
        //     return todos[index]
        // }
        // )
    }

    function removeTodo(id) {
        setTodos(todos.filter(todo => todo.id !== id))
    }

  return (
      <Context.Provider value={{removeTodo}}>
          <div className='wrapper'>
              <h1>First react example</h1>
              <AddTodo />
              {
                  todos.length ? <TodoList todos={todos} onToggle={toggleTodo}/> :
                  <p>No todos, input your todos</p>
              }

          </div>
      </Context.Provider>
  )
}

export default App;
