import React, {useContext} from 'react'
import PropTypes from 'prop-types'
import Contact from '../context'

const styles = {
    li: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '.5rem 1rem',
        border: '1px solid #ccc',
        borderRadius: '4px',
        marginBottom: '.5rem'
    },
    input: {
        marginRight: '1rem'
    }
}

function TodoItem({text, index, onChange}) {
    // return <li>{tex.text.title}</li>   ete {} chenk krm
    const { removeTodo } = useContext()
    const classes = []

    if (text.completed) {
        classes.push('done')
    }

    return (
        <li style={styles.li}>
            <span className={classes.join(' ')}>
                <input
                    type="checkbox"
                    checked={text.completed}
                    style={styles.input}
                    onChange={() => onChange(text.id)
                }/>
                <strong>{index + 1}</strong>
                &nbsp;
                {text.title}
            </span>

            <button className="rm">&times;</button>
        </li>)
}

TodoItem.propTypes = {
    text: PropTypes.object.isRequired,
    index: PropTypes.number,
    onChange: PropTypes.func
}

export default TodoItem
